import { useState } from "react";
import { Routes, Route, Navigate, useNavigate } from "react-router-dom";

import AdminDashboard from "./AdminDashboard";
import AdminReports from "./AdminReports";
import UserDashboard from "./UserDashboard";

/* ================= SAFE ROLE CHECK ================= */
const getRole = () => {
  const token = localStorage.getItem("token");
  if (!token) return null;

  try {
    const payload = JSON.parse(atob(token.split(".")[1]));
    return payload?.role || null;
  } catch {
    return null;
  }
};

export default function App() {
  const navigate = useNavigate();

  const [mode, setMode] = useState("USER_LOGIN");
  const [msg, setMsg] = useState("");

  const [userLogin, setUserLogin] = useState({ email: "", password: "" });
  const [adminLogin, setAdminLogin] = useState({ email: "", password: "" });

  const [register, setRegister] = useState({
    name: "",
    email: "",
    password: "",
    phone: "",
    vehicleNo: "",
    carType: ""
  });

  /* ================= LOGIN ================= */

  const loginUser = async () => {
    const res = await fetch("http://localhost:5000/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(userLogin)
    });

    const data = await res.json();

    if (data.token && data.role === "USER") {
      localStorage.setItem("token", data.token);
      navigate("/user");
    } else {
      setMsg("User login failed");
    }
  };

  const loginAdmin = async () => {
    const res = await fetch("http://localhost:5000/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(adminLogin)
    });

    const data = await res.json();

    if (data.token && data.role === "ADMIN") {
      localStorage.setItem("token", data.token);
      navigate("/admin");
    } else {
      setMsg("Admin access denied");
    }
  };

  const registerUser = async () => {
    const res = await fetch("http://localhost:5000/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(register)
    });
    const data = await res.json();
    setMsg(data.message || "User registered");
  };

  /* ================= LOGIN UI ================= */

  const LoginUI = () => (
    <div style={styles.page}>
      <h1 style={styles.title}>Smart Parking System</h1>

      <div style={styles.switch}>
        <button
          style={mode === "USER_LOGIN" ? styles.activeTab : styles.tab}
          onClick={() => setMode("USER_LOGIN")}
        >
          User Login
        </button>
        <button
          style={mode === "REGISTER" ? styles.activeTab : styles.tab}
          onClick={() => setMode("REGISTER")}
        >
          Register
        </button>
        <button
          style={mode === "ADMIN_LOGIN" ? styles.activeTab : styles.tab}
          onClick={() => setMode("ADMIN_LOGIN")}
        >
          Admin Login
        </button>
      </div>

      <div style={styles.card}>
        {mode === "USER_LOGIN" && (
          <>
            <input style={styles.input} placeholder="Email"
              onChange={e => setUserLogin({ ...userLogin, email: e.target.value })} />
            <input style={styles.input} type="password" placeholder="Password"
              onChange={e => setUserLogin({ ...userLogin, password: e.target.value })} />
            <button style={styles.primaryBtn} onClick={loginUser}>
              Login
            </button>
          </>
        )}

        {mode === "ADMIN_LOGIN" && (
          <>
            <input style={styles.input} placeholder="Admin Email"
              onChange={e => setAdminLogin({ ...adminLogin, email: e.target.value })} />
            <input style={styles.input} type="password" placeholder="Admin Password"
              onChange={e => setAdminLogin({ ...adminLogin, password: e.target.value })} />
            <button style={styles.adminBtn} onClick={loginAdmin}>
              Admin Login
            </button>
          </>
        )}

        {mode === "REGISTER" && (
          <>
            <input style={styles.input} placeholder="Name"
              onChange={e => setRegister({ ...register, name: e.target.value })} />
           <input
  style={styles.input}
  placeholder="Email"
  value={userLogin.email}
  onChange={e =>
    setUserLogin({ ...userLogin, email: e.target.value })
  }
/>

            <input style={styles.input} type="password" placeholder="Password"
              onChange={e => setRegister({ ...register, password: e.target.value })} />
            <input style={styles.input} placeholder="Phone"
              onChange={e => setRegister({ ...register, phone: e.target.value })} />
            <input style={styles.input} placeholder="Vehicle No"
              onChange={e => setRegister({ ...register, vehicleNo: e.target.value })} />
            <select style={styles.input}
              onChange={e => setRegister({ ...register, carType: e.target.value })}>
              <option value="">Car Type</option>
              <option>Sedan</option>
              <option>SUV</option>
              <option>EV</option>
            </select>
            <button style={styles.primaryBtn} onClick={registerUser}>
              Register
            </button>
          </>
        )}
      </div>

      {msg && <p style={{ color: "#fff" }}>{msg}</p>}
    </div>
  );

  /* ================= ROUTES ================= */

  return (
    <Routes>
      <Route path="/" element={<LoginUI />} />

      <Route
        path="/user"
        element={getRole() === "USER" ? <UserDashboard /> : <Navigate to="/" />}
      />

      <Route
        path="/admin"
        element={getRole() === "ADMIN" ? <AdminDashboard /> : <Navigate to="/" />}
      />

      <Route
        path="/admin/reports"
        element={getRole() === "ADMIN" ? <AdminReports /> : <Navigate to="/" />}
      />
    </Routes>
  );
}

/* ================= STYLES ================= */

const styles = {
  page: {
    minHeight: "100vh",
    background: "linear-gradient(135deg,#667eea,#764ba2)",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    paddingTop: "40px"
  },
  title: { color: "#fff" },
  switch: { display: "flex", gap: "10px", margin: "20px" },
  tab: { padding: "10px", borderRadius: "20px" },
  activeTab: { padding: "10px", borderRadius: "20px", fontWeight: "bold" },
  card: {
    background: "#fff",
    padding: "20px",
    borderRadius: "14px",
    width: "320px"
  },
  input: { width: "100%", padding: "10px", marginBottom: "10px" },
  primaryBtn: { width: "100%", padding: "10px", background: "#667eea", color: "#fff" },
  adminBtn: { width: "100%", padding: "10px", background: "#ef4444", color: "#fff" }
};
