import { useEffect, useState } from "react";
import "./AdminDashboard.css";

export default function AdminDashboard() {
  const token = localStorage.getItem("token");
  const auth = { Authorization: `Bearer ${token}` };

  /* ---------------- NAV ---------------- */
  const [tab, setTab] = useState("CITIES"); // CITIES | SLOTS | REPORTS

  /* ---------------- STATE ---------------- */
  const [cities, setCities] = useState([]);
  const [areas, setAreas] = useState([]);
  const [slots, setSlots] = useState([]);
  const [bookings, setBookings] = useState([]);

  const [cityName, setCityName] = useState("");
  const [areaForm, setAreaForm] = useState({ city: "", name: "" });
  const [slotForm, setSlotForm] = useState({
    city: "",
    area: "",
    slotNo: "",
    pricePerHour: ""
  });

  const [cancelId, setCancelId] = useState(null);
  const [reason, setReason] = useState("");
  const [search, setSearch] = useState("");

  /* ---------------- LOAD ---------------- */
  useEffect(() => {
    fetchAll();
  }, []);

  const fetchAll = () => {
    fetchCities();
    fetchAreas();
    fetchSlots();
    fetchBookings();
  };

  const fetchCities = async () => {
    const r = await fetch("http://localhost:5000/admin/cities", { headers: auth });
    const d = await r.json();
    setCities(d.map(c => c.name));
  };

  const fetchAreas = async () => {
    const r = await fetch("http://localhost:5000/admin/areas", { headers: auth });
    setAreas(await r.json());
  };

  const fetchSlots = async () => {
    const r = await fetch("http://localhost:5000/admin/slots", { headers: auth });
    setSlots(await r.json());
  };

  const fetchBookings = async () => {
    const r = await fetch("http://localhost:5000/admin/bookings", { headers: auth });
    setBookings(await r.json());
  };

  /* ---------------- CITY ---------------- */
  const addCity = async () => {
    if (!cityName) return;
    await fetch("http://localhost:5000/admin/cities", {
      method: "POST",
      headers: { "Content-Type": "application/json", ...auth },
      body: JSON.stringify({ name: cityName })
    });
    setCityName("");
    fetchAll();
  };

  const removeCity = async (name) => {
    if (!window.confirm("Delete city and all related data?")) return;
    await fetch(
      `http://localhost:5000/admin/cities/${encodeURIComponent(name)}`,
      { method: "DELETE", headers: auth }
    );
    fetchAll();
  };

  /* ---------------- AREA ---------------- */
  const addArea = async () => {
    if (!areaForm.city || !areaForm.name) return;
    await fetch("http://localhost:5000/admin/areas", {
      method: "POST",
      headers: { "Content-Type": "application/json", ...auth },
      body: JSON.stringify(areaForm)
    });
    setAreaForm({ city: "", name: "" });
    fetchAll();
  };

  const removeArea = async (city, name) => {
    if (!window.confirm("Delete area and its slots?")) return;
    await fetch(
      `http://localhost:5000/admin/areas/${city}/${name}`,
      { method: "DELETE", headers: auth }
    );
    fetchAll();
  };

  /* ---------------- SLOT ---------------- */
  const addSlot = async () => {
    await fetch("http://localhost:5000/admin/slots", {
      method: "POST",
      headers: { "Content-Type": "application/json", ...auth },
      body: JSON.stringify(slotForm)
    });
    setSlotForm({ city: "", area: "", slotNo: "", pricePerHour: "" });
    fetchSlots();
  };

  const toggleSlot = async (id) => {
    await fetch(`http://localhost:5000/admin/slots/${id}/toggle`, {
      method: "PUT",
      headers: auth
    });
    fetchSlots();
  };

  /* ---------------- BOOKINGS ---------------- */
  const cancelBooking = async () => {
    if (!reason.trim()) return;
    await fetch(
      `http://localhost:5000/admin/bookings/${cancelId}/cancel`,
      {
        method: "PUT",
        headers: { "Content-Type": "application/json", ...auth },
        body: JSON.stringify({ reason })
      }
    );
    setCancelId(null);
    setReason("");
    fetchBookings();
  };

  const filteredBookings = bookings.filter(
    b =>
      b.userId?.email?.toLowerCase().includes(search.toLowerCase()) ||
      b.slotId?.slotNo?.toLowerCase().includes(search.toLowerCase())
  );

  /* ---------------- UI ---------------- */
  return (
    <div className="admin-page">

      {/* HEADER */}
      <div className="admin-header">
        <div>
          <h2>Smart Parking</h2>
          <span>Admin Panel</span>
        </div>
        <button
          className="logout-icon-btn"
          onClick={() => {
            localStorage.clear();
            window.location.href = "/";
          }}
        >
          ⎋
        </button>
      </div>

      {/* NAVBAR */}
      <div className="admin-nav">
        <button onClick={() => setTab("CITIES")}>Cities & Areas</button>
        <button onClick={() => setTab("SLOTS")}>Parking Slots</button>
        <button onClick={() => setTab("REPORTS")}>Reports</button>
      </div>

      {/* -------- CITIES -------- */}
      {tab === "CITIES" && (
        <div className="card">
          <h3>City Management</h3>
          <input value={cityName} onChange={e => setCityName(e.target.value)} />
          <button onClick={addCity}>Add City</button>

          {cities.map(c => (
            <div className="row" key={c}>
              {c}
              <button onClick={() => removeCity(c)}>✖</button>
            </div>
          ))}

          <h3>Add Area</h3>
          <select onChange={e => setAreaForm({ ...areaForm, city: e.target.value })}>
            <option value="">City</option>
            {cities.map(c => <option key={c}>{c}</option>)}
          </select>
          <input
            placeholder="Area"
            value={areaForm.name}
            onChange={e => setAreaForm({ ...areaForm, name: e.target.value })}
          />
          <button onClick={addArea}>Add Area</button>

          {areas.map(a => (
            <div className="row" key={a._id}>
              {a.city} → {a.name}
              <button onClick={() => removeArea(a.city, a.name)}>✖</button>
            </div>
          ))}
        </div>
      )}

      {/* -------- SLOTS -------- */}
      {tab === "SLOTS" && (
        <div className="card">
          <h3>Add Slot</h3>
          <select onChange={e => setSlotForm({ ...slotForm, city: e.target.value })}>
            <option value="">City</option>
            {cities.map(c => <option key={c}>{c}</option>)}
          </select>

          <select onChange={e => setSlotForm({ ...slotForm, area: e.target.value })}>
            <option value="">Area</option>
            {areas.filter(a => a.city === slotForm.city)
              .map(a => <option key={a._id}>{a.name}</option>)}
          </select>

          <input
            placeholder="Slot No"
            value={slotForm.slotNo}
            onChange={e => setSlotForm({ ...slotForm, slotNo: e.target.value })}
          />
          <input
            placeholder="Price / Hr"
            value={slotForm.pricePerHour}
            onChange={e => setSlotForm({ ...slotForm, pricePerHour: e.target.value })}
          />
          <button onClick={addSlot}>Add Slot</button>

          <div className="slot-grid">
            {slots.map(s => (
              <div key={s._id} className={`slot-card ${s.isBooked ? "booked" : ""}`}>
                <h4>{s.slotNo}</h4>
                <p>{s.city} • {s.area}</p>
                <button onClick={() => toggleSlot(s._id)}>
                  {s.isEnabled ? "Disable" : "Enable"}
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* -------- REPORTS -------- */}
      {tab === "REPORTS" && (
        <div className="card">
          <h3>Booking Reports</h3>

          <input
            placeholder="Search user or slot"
            value={search}
            onChange={e => setSearch(e.target.value)}
          />

          <table>
            <thead>
              <tr>
                <th>User</th>
                <th>Slot</th>
                <th>Status</th>
                <th>Reason</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {filteredBookings.map(b => (
                <tr key={b._id}>
                  <td>{b.userId?.email}</td>
                  <td>{b.slotId?.slotNo}</td>
                  <td>{b.status}</td>
                  <td>{b.cancelReason || "-"}</td>
                  <td>
                    {b.status === "BOOKED" && (
                      <button onClick={() => setCancelId(b._id)}>Cancel</button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* CANCEL MODAL */}
      {cancelId && (
        <div className="modal">
          <div className="modal-card">
            <textarea
              placeholder="Cancellation reason"
              value={reason}
              onChange={e => setReason(e.target.value)}
            />
            <button onClick={cancelBooking}>Confirm</button>
            <button onClick={() => setCancelId(null)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}
