import "./AdminDashboard.css";
import { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import {
  FaParking,
  FaCar,
  FaChartBar,
  FaCog,
  FaSignOutAlt,
  FaCarSide,
  FaTools,
  FaCheckCircle
} from "react-icons/fa";

export default function AdminDashboard() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const auth = { Authorization: `Bearer ${token}` };

  const slotRef = useRef(null);
  const bookedRef = useRef(null);

  const [slots, setSlots] = useState([]);
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    fetchSlots();
    fetchBookings();
  }, []);

  const fetchSlots = async () => {
    const res = await fetch("http://localhost:5000/admin/slots", { headers: auth });
    setSlots(await res.json());
  };

  const fetchBookings = async () => {
    const res = await fetch("http://localhost:5000/admin/bookings", { headers: auth });
    const data = await res.json();
    setBookings(data.filter(b => b.slotId));
  };

  const toggleSlot = async (id) => {
    await fetch(`http://localhost:5000/admin/slots/${id}/toggle`, {
      method: "PUT",
      headers: auth
    });
    fetchSlots();
  };

  const updateStatus = async (id, status) => {
    await fetch(`http://localhost:5000/admin/slots/${id}/status`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        ...auth
      },
      body: JSON.stringify({ status })
    });
    fetchSlots();
  };

  const removeSlot = async (id) => {
    if (!window.confirm("Delete slot?")) return;
    await fetch(`http://localhost:5000/admin/slots/${id}`, {
      method: "DELETE",
      headers: auth
    });
    fetchSlots();
  };

  const totalRevenue = bookings.reduce((sum, b) => {
  const amt = Number(b.amount);
  return sum + (isNaN(amt) ? 0 : amt);
}, 0);


  const bookedSlots = slots.filter(s => s.isBooked || s.status === "BUSY");
  const freeSlots = slots.filter(s => !s.isBooked);

  return (
    <div className="admin-layout">

      {/* SIDEBAR */}
      <aside className="admin-sidebar">
        <h3>SmartPark</h3>

        <button onClick={() => slotRef.current.scrollIntoView({ behavior: "smooth" })}>
          <FaParking /> Parking
        </button>

        <button onClick={() => bookedRef.current.scrollIntoView({ behavior: "smooth" })}>
          <FaCar /> Booked
        </button>

        <button onClick={() => navigate("/admin/reports")}>
          <FaChartBar /> Reports
        </button>

        <button onClick={() => navigate("/admin/manage")}>
          <FaCog /> Manage
        </button>

        <button
          className="logout"
          onClick={() => {
            localStorage.clear();
            window.location.href = "/";
          }}
        >
          <FaSignOutAlt /> Logout
        </button>
      </aside>

      {/* CONTENT */}
      <main className="admin-content">

        <div className="admin-header">
          <h2>Admin Dashboard</h2>
        </div>

        <div className="stats-grid">
          <Stat title="Revenue">₹ {totalRevenue}</Stat>
          <Stat title="Slots">{slots.length}</Stat>
          <Stat title="Active">{bookedSlots.length}</Stat>
        </div>

        {/* FREE SLOTS */}
        <div ref={slotRef}>
          <h3>Parking Slots</h3>
          <div className="slot-grid">
            {freeSlots.map(s => (
              <div key={s._id} className={`slot-card ${!s.isEnabled ? "disabled" : ""}`}>
                <h4><FaCarSide /> {s.slotNo}</h4>
                <p>{s.city} • {s.area}</p>

               <p className={`status-pill ${s.status}`}>
  {String(s.status || "WORKING")}
</p>
 <span className={`status-pill ${s.status}`}>
                  {s.status === "WORKING" && <FaCheckCircle />}
                  {s.status === "UNDER_CONSTRUCTION" && <FaTools />}
                  {s.status}
                </span>

                <p>₹ {s.pricePerHour}/hr</p>

                <div className="actions">
                  <select
                    value={s.status}
                    onChange={e => updateStatus(s._id, e.target.value)}
                  >
                    <option value="WORKING">Working</option>
                    <option value="BUSY">Busy</option>
                    <option value="UNDER_CONSTRUCTION">Under Construction</option>
                  </select>

                  <button onClick={() => toggleSlot(s._id)}>
                    {s.isEnabled ? "Disable" : "Enable"}
                  </button>

                  <button className="btn-remove" onClick={() => removeSlot(s._id)}>
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* BOOKED */}
        <div ref={bookedRef}>
          <h3>Booked Slots</h3>
          <div className="slot-grid">
            {bookedSlots.map(s => {
              const booking = bookings.find(b => b.slotId?._id === s._id);
              return (
                <div key={s._id} className="slot-card booked">
                  <h4>{s.slotNo}</h4>
                  <p>{s.city} • {s.area}</p>

                  {booking && (
                    <div className="user-box">
                      <p>{booking.userId.email}</p>
                      <p>₹ {booking.amount}</p>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </div>

      </main>
    </div>
  );
}

const Stat = ({ title, children }) => (
  <div className="stat-card">
    <h4>{title}</h4>
    <p>{children}</p>
  </div>
);
