import { useState } from "react";
import { Routes, Route, Navigate, useNavigate } from "react-router-dom";

import AdminDashboard from "./AdminDashboard";
import AdminReports from "./AdminReports";
import UserDashboard from "./UserDashboard";
import AdminManage from "./AdminManage";

/* ================= ROLE CHECK ================= */
const getRole = () => {
  const token = localStorage.getItem("token");
  if (!token) return null;

  try {
    const payload = JSON.parse(atob(token.split(".")[1]));
    return payload?.role || null;
  } catch {
    return null;
  }
};

/* ================= LOGIN / REGISTER UI ================= */
function LoginUI({
  mode,
  setMode,
  userLogin,
  setUserLogin,
  adminLogin,
  setAdminLogin,
  register,
  setRegister,
  loginUser,
  loginAdmin,
  registerUser,
  msg
}) {
  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <h1 style={styles.title}>ðŸš— Smart Parking</h1>

        <div style={styles.switch}>
          <button onClick={() => setMode("USER_LOGIN")}>User</button>
          <button onClick={() => setMode("REGISTER")}>Register</button>
          <button onClick={() => setMode("ADMIN_LOGIN")}>Admin</button>
        </div>

        {mode === "USER_LOGIN" && (
          <>
            <input
              placeholder="Email"
              value={userLogin.email}
              onChange={e => setUserLogin({ ...userLogin, email: e.target.value })}
            />
            <input
              type="password"
              placeholder="Password"
              value={userLogin.password}
              onChange={e => setUserLogin({ ...userLogin, password: e.target.value })}
            />
            <button onClick={loginUser}>Login</button>
          </>
        )}

        {mode === "ADMIN_LOGIN" && (
          <>
            <input
              placeholder="Admin Email"
              value={adminLogin.email}
              onChange={e => setAdminLogin({ ...adminLogin, email: e.target.value })}
            />
            <input
              type="password"
              placeholder="Admin Password"
              value={adminLogin.password}
              onChange={e => setAdminLogin({ ...adminLogin, password: e.target.value })}
            />
            <button onClick={loginAdmin}>Admin Login</button>
          </>
        )}

        {mode === "REGISTER" && (
          <>
            <input
              placeholder="Full Name"
              value={register.name}
              onChange={e => setRegister({ ...register, name: e.target.value })}
            />

            <input
              placeholder="Email"
              value={register.email}
              onChange={e => setRegister({ ...register, email: e.target.value })}
            />

            <input
              type="password"
              placeholder="Password"
              value={register.password}
              onChange={e => setRegister({ ...register, password: e.target.value })}
            />

            <input
              placeholder="Phone Number"
              value={register.phone}
              onChange={e => setRegister({ ...register, phone: e.target.value })}
            />

            <input
              placeholder="Vehicle Number (eg: TS09AB1234)"
              value={register.vehicleNo}
              onChange={e => setRegister({ ...register, vehicleNo: e.target.value })}
            />

            <select
              value={register.vehicleType}
              onChange={e => setRegister({ ...register, vehicleType: e.target.value })}
            >
              <option value="">Vehicle Type</option>
              <option value="Car">Car</option>
              <option value="Bike">Bike</option>
              <option value="EV">EV</option>
            </select>

            <button onClick={registerUser}>Create Account</button>
          </>
        )}

        {msg && <p style={{ color: "green", marginTop: 10 }}>{msg}</p>}
      </div>
    </div>
  );
}

/* ================= APP ================= */
export default function App() {
  const navigate = useNavigate();

  const [mode, setMode] = useState("USER_LOGIN");
  const [msg, setMsg] = useState("");

  const [userLogin, setUserLogin] = useState({ email: "", password: "" });
  const [adminLogin, setAdminLogin] = useState({ email: "", password: "" });

  const [register, setRegister] = useState({
    name: "",
    email: "",
    password: "",
    phone: "",
    vehicleNo: "",
    vehicleType: ""
  });

  const loginUser = async () => {
    const res = await fetch("http://localhost:5000/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(userLogin)
    });
    const data = await res.json();
    if (data.token) {
      localStorage.setItem("token", data.token);
      navigate("/user");
    }
  };

  const loginAdmin = async () => {
    const res = await fetch("http://localhost:5000/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(adminLogin)
    });
    const data = await res.json();
    if (data.token) {
      localStorage.setItem("token", data.token);
      navigate("/admin");
    }
  };

  const registerUser = async () => {
    const res = await fetch("http://localhost:5000/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(register)
    });

    const data = await res.json();
    if (data.success) {
      setMsg("Account created. Login now.");
      setMode("USER_LOGIN");
    }
  };

  return (
    <Routes>
      <Route path="/" element={
        <LoginUI
          mode={mode}
          setMode={setMode}
          userLogin={userLogin}
          setUserLogin={setUserLogin}
          adminLogin={adminLogin}
          setAdminLogin={setAdminLogin}
          register={register}
          setRegister={setRegister}
          loginUser={loginUser}
          loginAdmin={loginAdmin}
          registerUser={registerUser}
          msg={msg}
        />
      }/>

      <Route path="/user" element={getRole() === "USER" ? <UserDashboard /> : <Navigate to="/" />} />
      <Route path="/admin" element={getRole() === "ADMIN" ? <AdminDashboard /> : <Navigate to="/" />} />
      <Route path="/admin/reports" element={getRole() === "ADMIN" ? <AdminReports /> : <Navigate to="/" />} />
      <Route path="/admin/manage" element={getRole() === "ADMIN" ? <AdminManage /> : <Navigate to="/" />} />
    </Routes>
  );
}

/* ================= UI STYLES ================= */
const styles = {
  page: {
    minHeight: "100vh",
    background: "linear-gradient(135deg,#1e3a8a,#312e81)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center"
  },
  card: {
    background: "#fff",
    padding: "30px",
    borderRadius: "16px",
    width: "350px",
    boxShadow: "0 20px 50px rgba(0,0,0,0.3)",
    display: "flex",
    flexDirection: "column",
    gap: "12px"
  },
  title: {
    textAlign: "center",
    marginBottom: "10px"
  },
  switch: {
    display: "flex",
    gap: "8px",
    justifyContent: "center"
  }
};
