import "./AdminDashboard.css";
import { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";


export default function AdminDashboard() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const authHeader = { Authorization: `Bearer ${token}` };
  const cityRef = useRef(null);
const slotRef = useRef(null);
const bookedRef = useRef(null);


  /* ---------------- STATE ---------------- */
  const [cities, setCities] = useState([]);      // ["Hyderabad"]
  const [areas, setAreas] = useState([]);        // [{ city, name }]
  const [slots, setSlots] = useState([]);
  const [bookings, setBookings] = useState([]);

  const [cityName, setCityName] = useState("");
  const [areaForm, setAreaForm] = useState({ city: "", name: "" });
  const [slotForm, setSlotForm] = useState({
    city: "",
    area: "",
    slotNo: "",
    pricePerHour: ""
  });

  /* ---------------- LOAD DATA ---------------- */
  useEffect(() => {
    fetchCities();
    fetchAreas();
    fetchSlots();
    fetchBookings();
  }, []);

  /* ---------------- CITY ---------------- */
  const fetchCities = async () => {
    const res = await fetch("http://localhost:5000/admin/cities", {
      headers: authHeader
    });
    const data = await res.json();
    setCities(data.map(c => c.name));
  };

  const addCity = async () => {
    if (!cityName || cities.includes(cityName)) {
      alert("City already exists or empty");
      return;
    }

    await fetch("http://localhost:5000/admin/cities", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        ...authHeader
      },
      body: JSON.stringify({ name: cityName })
    });

    setCityName("");
    fetchCities();
  };

  const removeCity = async (city) => {
    if (!window.confirm(`Remove city "${city}"?`)) return;

    await fetch(
      `http://localhost:5000/admin/cities/${encodeURIComponent(city)}`,
      { method: "DELETE", headers: authHeader }
    );

    fetchCities();
    fetchAreas();
    fetchSlots();
  };

  /* ---------------- AREA ---------------- */
  const fetchAreas = async () => {
    const res = await fetch("http://localhost:5000/admin/areas", {
      headers: authHeader
    });
    setAreas(await res.json());
  };

  const addArea = async () => {
    if (!areaForm.city || !areaForm.name) {
      alert("Select city and enter area");
      return;
    }

    if (areas.some(a => a.city === areaForm.city && a.name === areaForm.name)) {
      alert("Area already exists");
      return;
    }

    await fetch("http://localhost:5000/admin/areas", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        ...authHeader
      },
      body: JSON.stringify(areaForm)
    });

    setAreaForm({ city: "", name: "" });
    fetchAreas();
  };

  const removeArea = async (city, name) => {
    if (!window.confirm(`Remove area "${name}"?`)) return;

    await fetch(
      `http://localhost:5000/admin/areas/${encodeURIComponent(city)}/${encodeURIComponent(name)}`,
      { method: "DELETE", headers: authHeader }
    );

    fetchAreas();
    fetchSlots();
  };

  /* ---------------- SLOT ---------------- */
  const fetchSlots = async () => {
    const res = await fetch("http://localhost:5000/admin/slots", {
      headers: authHeader
    });
    setSlots(await res.json());
  };

  const addSlot = async () => {
    if (
      !slotForm.city ||
      !slotForm.area ||
      !slotForm.slotNo ||
      !slotForm.pricePerHour
    ) {
      alert("Fill all slot fields");
      return;
    }

    // frontend duplicate safety
    if (
      slots.some(
        s =>
          s.city === slotForm.city &&
          s.area === slotForm.area &&
          s.slotNo === slotForm.slotNo
      )
    ) {
      alert("Slot already exists");
      return;
    }

    const res = await fetch("http://localhost:5000/admin/slots", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        ...authHeader
      },
      body: JSON.stringify({
        city: slotForm.city,
        area: slotForm.area,
        slotNo: slotForm.slotNo,
        pricePerHour: Number(slotForm.pricePerHour)
      })
    });

    if (!res.ok) {
      const err = await res.json();
      alert(err.message || "Failed to add slot");
      return;
    }

    setSlotForm({ city: "", area: "", slotNo: "", pricePerHour: "" });
    fetchSlots();
  };

  const toggleSlot = async (id) => {
    await fetch(`http://localhost:5000/admin/slots/${id}/toggle`, {
      method: "PUT",
      headers: authHeader
    });
    fetchSlots();
  };

  const removeSlot = async (id, slotNo) => {
    if (!window.confirm(`Remove slot ${slotNo}?`)) return;

    await fetch(`http://localhost:5000/admin/slots/${id}`, {
      method: "DELETE",
      headers: authHeader
    });

    fetchSlots();
  };

  /* ---------------- BOOKINGS / ANALYTICS ---------------- */
  const fetchBookings = async () => {
    const res = await fetch("http://localhost:5000/admin/bookings", {
      headers: authHeader
    });
    const data = await res.json();
    setBookings(data.filter(b => b.slotId));
  };

  const totalRevenue = bookings.reduce((sum, b) => sum + b.amount, 0);

  const bookingBySlotId = {};
bookings.forEach(b => {
  bookingBySlotId[b.slotId._id] = b;
});

const bookedSlots = slots.filter(s => s.isBooked);
const freeSlots = slots.filter(s => !s.isBooked);

  /* ---------------- UI ---------------- */
 return (
  <div className="admin-layout">

    {/* SIDEBAR */}
    <aside className="admin-sidebar">
  <div className="sidebar-header">
    <h2>SmartPark</h2>
    <span>Admin Panel</span>
