import "./AdminDashboard.css";
import { useEffect, useState, useRef } from "react";


export default function AdminDashboard() {
  const token = localStorage.getItem("token");
  const authHeader = { Authorization: `Bearer ${token}` };
  const cityRef = useRef(null);
const slotRef = useRef(null);
const bookedRef = useRef(null);


  /* ---------------- STATE ---------------- */
  const [cities, setCities] = useState([]);      // ["Hyderabad"]
  const [areas, setAreas] = useState([]);        // [{ city, name }]
  const [slots, setSlots] = useState([]);
  const [bookings, setBookings] = useState([]);

  const [cityName, setCityName] = useState("");
  const [areaForm, setAreaForm] = useState({ city: "", name: "" });
  const [slotForm, setSlotForm] = useState({
    city: "",
    area: "",
    slotNo: "",
    pricePerHour: ""
  });

  /* ---------------- LOAD DATA ---------------- */
  useEffect(() => {
    fetchCities();
    fetchAreas();
    fetchSlots();
    fetchBookings();
  }, []);

  /* ---------------- CITY ---------------- */
  const fetchCities = async () => {
    const res = await fetch("http://localhost:5000/admin/cities", {
      headers: authHeader
    });
    const data = await res.json();
    setCities(data.map(c => c.name));
  };

  const addCity = async () => {
    if (!cityName || cities.includes(cityName)) {
      alert("City already exists or empty");
      return;
    }

    await fetch("http://localhost:5000/admin/cities", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        ...authHeader
      },
      body: JSON.stringify({ name: cityName })
    });

    setCityName("");
    fetchCities();
  };

  const removeCity = async (city) => {
    if (!window.confirm(`Remove city "${city}"?`)) return;

    await fetch(
      `http://localhost:5000/admin/cities/${encodeURIComponent(city)}`,
      { method: "DELETE", headers: authHeader }
    );

    fetchCities();
    fetchAreas();
    fetchSlots();
  };

  /* ---------------- AREA ---------------- */
  const fetchAreas = async () => {
    const res = await fetch("http://localhost:5000/admin/areas", {
      headers: authHeader
    });
    setAreas(await res.json());
  };

  const addArea = async () => {
    if (!areaForm.city || !areaForm.name) {
      alert("Select city and enter area");
      return;
    }

    if (areas.some(a => a.city === areaForm.city && a.name === areaForm.name)) {
      alert("Area already exists");
      return;
    }

    await fetch("http://localhost:5000/admin/areas", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        ...authHeader
      },
      body: JSON.stringify(areaForm)
    });

    setAreaForm({ city: "", name: "" });
    fetchAreas();
  };

  const removeArea = async (city, name) => {
    if (!window.confirm(`Remove area "${name}"?`)) return;

    await fetch(
      `http://localhost:5000/admin/areas/${encodeURIComponent(city)}/${encodeURIComponent(name)}`,
      { method: "DELETE", headers: authHeader }
    );

    fetchAreas();
    fetchSlots();
  };

  /* ---------------- SLOT ---------------- */
  const fetchSlots = async () => {
    const res = await fetch("http://localhost:5000/admin/slots", {
      headers: authHeader
    });
    setSlots(await res.json());
  };

  const addSlot = async () => {
    if (
      !slotForm.city ||
      !slotForm.area ||
      !slotForm.slotNo ||
      !slotForm.pricePerHour
    ) {
      alert("Fill all slot fields");
      return;
    }

    // frontend duplicate safety
    if (
      slots.some(
        s =>
          s.city === slotForm.city &&
          s.area === slotForm.area &&
          s.slotNo === slotForm.slotNo
      )
    ) {
      alert("Slot already exists");
      return;
    }

    const res = await fetch("http://localhost:5000/admin/slots", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        ...authHeader
      },
      body: JSON.stringify({
        city: slotForm.city,
        area: slotForm.area,
        slotNo: slotForm.slotNo,
        pricePerHour: Number(slotForm.pricePerHour)
      })
    });

    if (!res.ok) {
      const err = await res.json();
      alert(err.message || "Failed to add slot");
      return;
    }

    setSlotForm({ city: "", area: "", slotNo: "", pricePerHour: "" });
    fetchSlots();
  };

  const toggleSlot = async (id) => {
    await fetch(`http://localhost:5000/admin/slots/${id}/toggle`, {
      method: "PUT",
      headers: authHeader
    });
    fetchSlots();
  };

  const removeSlot = async (id, slotNo) => {
    if (!window.confirm(`Remove slot ${slotNo}?`)) return;

    await fetch(`http://localhost:5000/admin/slots/${id}`, {
      method: "DELETE",
      headers: authHeader
    });

    fetchSlots();
  };

  /* ---------------- BOOKINGS / ANALYTICS ---------------- */
  const fetchBookings = async () => {
    const res = await fetch("http://localhost:5000/admin/bookings", {
      headers: authHeader
    });
    const data = await res.json();
    setBookings(data.filter(b => b.slotId));
  };

  const totalRevenue = bookings.reduce((sum, b) => sum + b.amount, 0);

  const bookingBySlotId = {};
bookings.forEach(b => {
  bookingBySlotId[b.slotId._id] = b;
});

const bookedSlots = slots.filter(s => s.isBooked);
const freeSlots = slots.filter(s => !s.isBooked);

  /* ---------------- UI ---------------- */
 return (
  <div className="admin-layout">

    {/* SIDEBAR */}
    <aside className="admin-sidebar">
      <h3>Admin</h3>

      <button onClick={() => cityRef.current.scrollIntoView({ behavior: "smooth" })}>
        City & Area
      </button>

      <button onClick={() => slotRef.current.scrollIntoView({ behavior: "smooth" })}>
        Parking Slots
      </button>

      <button onClick={() => bookedRef.current.scrollIntoView({ behavior: "smooth" })}>
        Booked Slots
      </button>

      <hr />

      <button onClick={() => window.location.href = "/admin/reports"}>
        Reports
      </button>

      <button
        className="logout"
        onClick={() => {
          localStorage.removeItem("token");
          window.location.href = "/";
        }}
      >
        Logout
      </button>
    </aside>

    {/* MAIN CONTENT */}
    <main className="admin-content">

      {/* HEADER */}
      <div className="admin-header">
        <div className="admin-title">
          <h1>Smart Parking</h1>
          <span>Admin Dashboard</span>
        </div>
      </div>

      {/* STATS */}
      <div className="stats-grid">
        <Stat title="Total Revenue">₹ {totalRevenue}</Stat>
        <Stat title="Cities">{cities.length}</Stat>
        <Stat title="Areas">{areas.length}</Stat>
        <Stat title="Slots">{slots.length}</Stat>
      </div>

      {/* USAGE */}
      <div className="card">
        <h3>Usage Summary (Live)</h3>
        <p>Total Bookings: {bookings.length}</p>
        <p>
          Avg Duration:{" "}
          {(bookings.reduce((s, b) => s + b.hours, 0) / bookings.length || 0).toFixed(2)} hrs
        </p>
      </div>

      {/* CITY + AREA */}
      <div ref={cityRef} className="grid-2">
        <div className="card">
          <h3>City Management</h3>
          <input
            placeholder="Enter city name"
            value={cityName}
            onChange={e => setCityName(e.target.value)}
          />
          <button onClick={addCity}>Add City</button>
        </div>

        <div className="card">
          <h3>Area Management</h3>
          <select
            value={areaForm.city}
            onChange={e => setAreaForm({ ...areaForm, city: e.target.value })}
          >
            <option value="">Select City</option>
            {cities.map(c => <option key={c}>{c}</option>)}
          </select>

          <input
            placeholder="Enter area name"
            value={areaForm.name}
            onChange={e => setAreaForm({ ...areaForm, name: e.target.value })}
          />
          <button onClick={addArea}>Add Area</button>
        </div>
      </div>

      {/* SLOT MANAGEMENT */}
      <div ref={slotRef} className="card">
        <h3>Add Parking Slot</h3>

        <div className="grid-4">
          <select
            value={slotForm.city}
            onChange={e => setSlotForm({ ...slotForm, city: e.target.value })}
          >
            <option value="">City</option>
            {cities.map(c => <option key={c}>{c}</option>)}
          </select>

          <select
            value={slotForm.area}
            onChange={e => setSlotForm({ ...slotForm, area: e.target.value })}
          >
            <option value="">Area</option>
            {areas.filter(a => a.city === slotForm.city)
              .map(a => <option key={a.name}>{a.name}</option>)}
          </select>

          <input
            placeholder="Slot No"
            value={slotForm.slotNo}
            onChange={e => setSlotForm({ ...slotForm, slotNo: e.target.value })}
          />

          <input
            type="number"
            placeholder="Price / Hour"
            value={slotForm.pricePerHour}
            onChange={e => setSlotForm({ ...slotForm, pricePerHour: e.target.value })}
          />
        </div>

        <button onClick={addSlot}>Add Slot</button>
      </div>

      {/* FREE SLOTS */}
      <h2>Parking Slots</h2>
      <div className="slot-grid">
        {freeSlots.map(s => (
          <div key={s._id} className={`slot-card ${!s.isEnabled ? "disabled" : ""}`}>
            <h4>{s.slotNo}</h4>
            <p>{s.city}</p>
            <p>{s.area}</p>
            <p>₹ {s.pricePerHour}/hr</p>

            <div className="actions">
              <button onClick={() => toggleSlot(s._id)}>
                {s.isEnabled ? "Disable" : "Enable"}
              </button>
              <button onClick={() => removeSlot(s._id, s.slotNo)}>Remove</button>
            </div>
          </div>
        ))}
      </div>

      {/* BOOKED SLOTS */}
      <div ref={bookedRef}>
        <h2 style={{ color: "#dc2626" }}>Booked Slots</h2>
        <div className="slot-grid">
          {bookedSlots.map(s => {
            const booking = bookings.find(b => b.slotId._id === s._id);
            return (
              <div key={s._id} className="slot-card booked">
                <h4>{s.slotNo}</h4>
                <p>{s.city}</p>
                <p>{s.area}</p>

                {booking && (
                  <div className="user-box">
                    <p><b>User:</b> {booking.userId.email}</p>
                    <p><b>Phone:</b> {booking.userId.phone}</p>
                    <p><b>Vehicle:</b> {booking.userId.vehicleNo}</p>
                    <p><b>Paid:</b> ₹ {booking.amount}</p>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>

    </main>
  </div>
);
}
/* ---------------- SMALL CARD ---------------- */
const Stat = ({ title, children }) => (
  <div className="stat-card">
    <h4>{title}</h4>
    <p>{children}</p>
  </div>
);
