import { useState } from "react";
import { Routes, Route, Navigate, useNavigate } from "react-router-dom";

import AdminDashboard from "./AdminDashboard";
import AdminReports from "./AdminReports";
import UserDashboard from "./UserDashboard";

/* ================= SAFE ROLE CHECK ================= */
const getRole = () => {
  const token = localStorage.getItem("token");
  if (!token) return null;

  try {
    const payload = JSON.parse(atob(token.split(".")[1]));
    return payload?.role || null;
  } catch {
    return null;
  }
};

/* ================= LOGIN UI COMPONENT ================= */
function LoginUI({
  mode,
  setMode,
  userLogin,
  setUserLogin,
  adminLogin,
  setAdminLogin,
  register,
  setRegister,
  loginUser,
  loginAdmin,
  registerUser,
  msg
}) {
  return (
    <div style={styles.page}>
      <h1 style={styles.title}>Smart Parking System</h1>

      <div style={styles.switch}>
        <button type="button" onClick={() => setMode("USER_LOGIN")}>
          User Login
        </button>
        <button type="button" onClick={() => setMode("REGISTER")}>
          Register
        </button>
        <button type="button" onClick={() => setMode("ADMIN_LOGIN")}>
          Admin Login
        </button>
      </div>

      <div style={styles.card}>
        {mode === "USER_LOGIN" && (
          <>
            <input
              placeholder="Email"
              value={userLogin.email}
              onChange={e =>
                setUserLogin({ ...userLogin, email: e.target.value })
              }
            />
            <input
              type="password"
              placeholder="Password"
              value={userLogin.password}
              onChange={e =>
                setUserLogin({ ...userLogin, password: e.target.value })
              }
            />
            <button type="button" onClick={loginUser}>
              Login
            </button>
          </>
        )}

        {mode === "ADMIN_LOGIN" && (
          <>
            <input
              placeholder="Admin Email"
              value={adminLogin.email}
              onChange={e =>
                setAdminLogin({ ...adminLogin, email: e.target.value })
              }
            />
            <input
              type="password"
              placeholder="Admin Password"
              value={adminLogin.password}
              onChange={e =>
                setAdminLogin({ ...adminLogin, password: e.target.value })
              }
            />
            <button type="button" onClick={loginAdmin}>
              Admin Login
            </button>
          </>
        )}

        {mode === "REGISTER" && (
          <>
            <input
              placeholder="Name"
              value={register.name}
              onChange={e =>
                setRegister({ ...register, name: e.target.value })
              }
            />
            <input
              placeholder="Email"
              value={register.email}
              onChange={e =>
                setRegister({ ...register, email: e.target.value })
              }
            />
            <input
              type="password"
              placeholder="Password"
              value={register.password}
              onChange={e =>
                setRegister({ ...register, password: e.target.value })
              }
            />
            <button type="button" onClick={registerUser}>
              Register
            </button>
          </>
        )}
      </div>

      {msg && <p style={{ color: "#fff" }}>{msg}</p>}
    </div>
  );
}

/* ================= APP ================= */
export default function App() {
  const navigate = useNavigate();

  const [mode, setMode] = useState("USER_LOGIN");
  const [msg, setMsg] = useState("");

  const [userLogin, setUserLogin] = useState({ email: "", password: "" });
  const [adminLogin, setAdminLogin] = useState({ email: "", password: "" });
  const [register, setRegister] = useState({
    name: "",
    email: "",
    password: ""
  });

  const loginUser = async () => {
    const res = await fetch("http://localhost:5000/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(userLogin)
    });
    const data = await res.json();
    if (data.token) {
      localStorage.setItem("token", data.token);
      navigate("/user");
    }
  };

  const loginAdmin = async () => {
    const res = await fetch("http://localhost:5000/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(adminLogin)
    });
    const data = await res.json();
    if (data.token) {
      localStorage.setItem("token", data.token);
      navigate("/admin");
    }
  };

  const registerUser = async () => {
    await fetch("http://localhost:5000/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(register)
    });
    setMsg("Registered successfully");
  };

  return (
    <Routes>
      <Route
        path="/"
        element={
          <LoginUI
            mode={mode}
            setMode={setMode}
            userLogin={userLogin}
            setUserLogin={setUserLogin}
            adminLogin={adminLogin}
            setAdminLogin={setAdminLogin}
            register={register}
            setRegister={setRegister}
            loginUser={loginUser}
            loginAdmin={loginAdmin}
            registerUser={registerUser}
            msg={msg}
          />
        }
      />
      <Route
        path="/user"
        element={getRole() === "USER" ? <UserDashboard /> : <Navigate to="/" />}
      />
      <Route
        path="/admin"
        element={getRole() === "ADMIN" ? <AdminDashboard /> : <Navigate to="/" />}
      />
      <Route
        path="/admin/reports"
        element={
          getRole() === "ADMIN" ? <AdminReports /> : <Navigate to="/" />
        }
      />
    </Routes>
  );
}

/* ================= STYLES ================= */
const styles = {
  page: {
    minHeight: "100vh",
    background: "linear-gradient(135deg,#667eea,#764ba2)",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    paddingTop: "40px"
  },
  title: { color: "#fff" },
  switch: { display: "flex", gap: "10px", marginBottom: "20px" },
  card: {
    background: "#fff",
    padding: "20px",
    borderRadius: "12px",
    width: "320px"
  }
};
