import { useEffect, useState } from "react";

export default function AdminReports() {
  const token = localStorage.getItem("token");

  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [slotId, setSlotId] = useState("");
  const [segment, setSegment] = useState("");
  const [slots, setSlots] = useState([]);
  const [report, setReport] = useState(null);
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/admin/slots", {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(setSlots);
  }, []);

  const generate = async () => {
    const res = await fetch(
      `http://localhost:5000/admin/reports?from=${from}&to=${to}&slotId=${slotId}&segment=${segment}`,
      { headers: { Authorization: `Bearer ${token}` } }
    );
    setReport(await res.json());
  };

  const exportCSV = () => {
    window.open(
      `http://localhost:5000/admin/reports/export?from=${from}&to=${to}&slotId=${slotId}`,
      "_blank"
    );
  };

  return (
    <div style={{ padding: 30 }}>
      <h2>Admin Reports</h2>

      {/* FILTERS */}
      <div style={{ display: "flex", gap: 10, marginBottom: 15 }}>
        <input type="date" onChange={e => setFrom(e.target.value)} />
        <input type="date" onChange={e => setTo(e.target.value)} />

        <select onChange={e => setSlotId(e.target.value)}>
          <option value="">All Slots</option>
          {slots.map(s => (
            <option key={s._id} value={s._id}>
              {s.slotNo} ({s.city}-{s.area})
            </option>
          ))}
        </select>

        <select onChange={e => setSegment(e.target.value)}>
          <option value="">No Segment</option>
          <option value="day">By Day</option>
          <option value="week">By Week</option>
          <option value="user">By User Type</option>
        </select>

        <button onClick={generate}>Generate</button>
        <button onClick={exportCSV}>Export CSV</button>
      </div>

      {/* SUMMARY */}
      {report && (
        <>
          <p>Total Bookings: {report.summary.totalBookings}</p>
          <p>Average Duration: {report.summary.avgDuration} hrs</p>
          <p>Peak Hour: {report.summary.peakHour}:00</p>

          {/* SEARCH */}
          <input
            placeholder="Search user / slot"
            onChange={e => setSearch(e.target.value)}
          />

          {/* TABLE */}
          <table border="1" cellPadding="8" style={{ marginTop: 10 }}>
            <thead>
              <tr>
                <th>User</th>
                <th>Slot</th>
                <th>Area</th>
                <th>Hours</th>
                <th>Amount</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {report.bookings
                .filter(
                  b =>
                    b.userId.email.includes(search) ||
                    b.slotId.slotNo.includes(search)
                )
                .map(b => (
                  <tr key={b._id}>
                    <td>{b.userId.email}</td>
                    <td>{b.slotId.slotNo}</td>
                    <td>{b.slotId.city} - {b.slotId.area}</td>
                    <td>{b.hours}</td>
                    <td>â‚¹ {b.amount}</td>
                    <td>{b.status}</td>
                  </tr>
                ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}
