import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./AdminReports.css";

export default function AdminReports() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const auth = { Authorization: `Bearer ${token}` };

  const [bookings, setBookings] = useState([]);
  const [cities, setCities] = useState([]);
  const [areas, setAreas] = useState([]);

  const [search, setSearch] = useState("");
  const [cancelId, setCancelId] = useState(null);
  const [reason, setReason] = useState("");

  /* ---------------- LOAD DATA ---------------- */
  useEffect(() => {
    fetchBookings();
    fetchCities();
    fetchAreas();
  }, []);

  const fetchBookings = async () => {
    const res = await fetch("http://localhost:5000/admin/bookings", {
      headers: auth
    });
    setBookings(await res.json());
  };

  const fetchCities = async () => {
    const res = await fetch("http://localhost:5000/admin/cities", {
      headers: auth
    });
    setCities(await res.json());
  };

  const fetchAreas = async () => {
    const res = await fetch("http://localhost:5000/admin/areas", {
      headers: auth
    });
    setAreas(await res.json());
  };

  /* ---------------- CANCEL BOOKING ---------------- */
  const cancelBooking = async () => {
    if (!reason.trim()) {
      alert("Cancellation reason required");
      return;
    }

    await fetch(
      `http://localhost:5000/admin/bookings/${cancelId}/cancel`,
      {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          ...auth
        },
        body: JSON.stringify({ reason })
      }
    );

    setCancelId(null);
    setReason("");
    fetchBookings();
  };

  /* ---------------- DELETE CITY / AREA ---------------- */
  const removeCity = async (name) => {
    if (!window.confirm(`Delete city "${name}"?`)) return;
    await fetch(
      `http://localhost:5000/admin/cities/${encodeURIComponent(name)}`,
      { method: "DELETE", headers: auth }
    );
    fetchCities();
    fetchAreas();
    fetchBookings();
  };

  const removeArea = async (city, name) => {
    if (!window.confirm(`Delete area "${name}"?`)) return;
    await fetch(
      `http://localhost:5000/admin/areas/${encodeURIComponent(city)}/${encodeURIComponent(name)}`,
      { method: "DELETE", headers: auth }
    );
    fetchAreas();
    fetchBookings();
  };

  /* ---------------- FILTER ---------------- */
  const filteredBookings = bookings.filter(b =>
    b.userId?.email?.toLowerCase().includes(search.toLowerCase()) ||
    b.slotId?.slotNo?.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="admin-report-page">
      {/* HEADER */}
      <div className="admin-header">
        <div>
          <h2>Admin Reports</h2>
          <span>Bookings, cancellations & management</span>
        </div>
        <button className="back-btn" onClick={() => navigate("/admin")}>
          ← Back
        </button>
      </div>

      {/* SEARCH */}
      <input
        className="search"
        placeholder="Search by user email or slot"
        value={search}
        onChange={e => setSearch(e.target.value)}
      />

      {/* BOOKINGS TABLE */}
      <div className="table-card">
        <table>
          <thead>
            <tr>
              <th>User</th>
              <th>Slot</th>
              <th>City</th>
              <th>Area</th>
              <th>Status</th>
              <th>Reason</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {filteredBookings.map(b => (
              <tr key={b._id}>
                <td>{b.userId?.email}</td>
                <td>{b.slotId?.slotNo}</td>
                <td>{b.slotId?.city}</td>
                <td>{b.slotId?.area}</td>
                <td className={`status ${b.status.toLowerCase()}`}>
                  {b.status}
                </td>
                <td>{b.cancelReason || "-"}</td>
                <td>
                  {b.status === "BOOKED" && (
                    <button
                      className="danger"
                      onClick={() => setCancelId(b._id)}
                    >
                      Cancel
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* CITY MANAGEMENT */}
      <div className="manage-card">
        <h3>Remove City</h3>
        {cities.map(c => (
          <div key={c.name} className="row">
            <span>{c.name}</span>
            <button onClick={() => removeCity(c.name)}>✖</button>
          </div>
        ))}
      </div>

      {/* AREA MANAGEMENT */}
      <div className="manage-card">
        <h3>Remove Area</h3>
        {areas.map(a => (
          <div key={a._id} className="row">
            <span>{a.city} → {a.name}</span>
            <button onClick={() => removeArea(a.city, a.name)}>✖</button>
          </div>
        ))}
      </div>

      {/* CANCEL MODAL */}
      {cancelId && (
        <div className="modal">
          <div className="modal-card">
            <h3>Cancel Booking</h3>
            <textarea
              placeholder="Enter cancellation reason"
              value={reason}
              onChange={e => setReason(e.target.value)}
            />
            <div className="modal-actions">
              <button onClick={() => setCancelId(null)}>Close</button>
              <button className="danger" onClick={cancelBooking}>
                Confirm Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
