import { useEffect, useState } from "react";
import "./UserDashboard.css";

export default function UserDashboard() {
  const token = localStorage.getItem("token");
  const authHeader = { Authorization: `Bearer ${token}` };

  const [slots, setSlots] = useState([]);
  const [bookings, setBookings] = useState([]);

  const [city, setCity] = useState("");
  const [area, setArea] = useState("");

  const [fromTime, setFromTime] = useState("");
  const [toTime, setToTime] = useState("");

  const [showPay, setShowPay] = useState(false);
  const [paySlot, setPaySlot] = useState(null);
  const [method, setMethod] = useState("UPI");

  /* ---------------- LOAD DATA ---------------- */
  useEffect(() => {
    fetchSlots();
    fetchBookings();
  }, []);

  const fetchSlots = async () => {
    const res = await fetch("http://localhost:5000/slots", {
      headers: authHeader
    });
    setSlots(await res.json());
  };

  const fetchBookings = async () => {
    const res = await fetch("http://localhost:5000/bookings/my", {
      headers: authHeader
    });
    const data = await res.json();

    // ✅ FILTER NULL SLOT BOOKINGS
    setBookings(
      data
        .filter(b => b.slotId)
        .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
    );
  };

  /* ---------------- PAYMENT ---------------- */
  const confirmPayment = async () => {
    if (!fromTime || !toTime) {
      alert("Please select booking time");
      return;
    }

    await fetch("http://localhost:5000/bookings", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        ...authHeader
      },
      body: JSON.stringify({
        slotId: paySlot._id,
        fromTime,
        toTime,
        paymentMethod: method
      })
    });

    setShowPay(false);
    setPaySlot(null);
    fetchSlots();
    fetchBookings();
  };

  const extendBooking = async (id) => {
    await fetch(`http://localhost:5000/bookings/${id}/extend`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        ...authHeader
      },
      body: JSON.stringify({ extraHours: 1 })
    });
    fetchBookings();
  };

  const cancelBooking = async (id) => {
    await fetch(`http://localhost:5000/bookings/${id}/cancel`, {
      method: "PUT",
      headers: authHeader
    });
    fetchSlots();
    fetchBookings();
  };

  /* ---------------- FILTER DATA ---------------- */

  const cities = [...new Set(slots.map(s => s.city))];

  const areas = [
    ...new Set(
      slots
        .filter(s => !city || s.city === city)
        .map(s => s.area)
    )
  ];

  const availableSlots = slots.filter(
    s =>
      !s.isBooked &&
      s.isEnabled &&
      (!city || s.city === city) &&
      (!area || s.area === area)
  );

  /* ---------------- UI ---------------- */
  return (
    <div className="user-page">

      {/* HEADER */}
      <div className="user-header">
        <div>
          <h2>Smart Parking</h2>
          <span>User Dashboard</span>
        </div>

        <button
          className="logout-icon-btn"
          onClick={() => {
            localStorage.clear();
            window.location.href = "/";
          }}
        >
          ⎋
        </button>
      </div>

      {/* FILTERS */}
      <div className="filters">
        <select value={city} onChange={e => {
          setCity(e.target.value);
          setArea("");
        }}>
          <option value="">All Cities</option>
          {cities.map(c => (
            <option key={c} value={c}>{c}</option>
          ))}
        </select>

        <select value={area} onChange={e => setArea(e.target.value)}>
          <option value="">All Areas</option>
          {areas.map(a => (
            <option key={a} value={a}>{a}</option>
          ))}
        </select>

        <input type="datetime-local" onChange={e => setFromTime(e.target.value)} />
        <input type="datetime-local" onChange={e => setToTime(e.target.value)} />
      </div>

      {/* AVAILABLE SLOTS */}
      <h3>Available Slots</h3>
      <div className="slot-grid">
        {availableSlots.map(s => (
          <div className="slot-card" key={s._id}>
            <h4>{s.slotNo}</h4>
            <p>{s.city} • {s.area}</p>
            <p>₹ {s.pricePerHour}/hr</p>

            <button onClick={() => {
              setPaySlot(s);
              setShowPay(true);
            }}>
              Book Slot
            </button>
          </div>
        ))}
      </div>

      {/* PAYMENT MODAL */}
      {showPay && paySlot && (
        <div className="modal">
          <div className="modal-card">
            <h3>Payment</h3>

            <select value={method} onChange={e => setMethod(e.target.value)}>
              <option value="UPI">UPI</option>
              <option value="CARD">Card</option>
            </select>

            <button onClick={confirmPayment}>
              Pay & Book
            </button>

            <button className="cancel" onClick={() => setShowPay(false)}>
              Cancel
            </button>
          </div>
        </div>
      )}

      {/* BOOKING HISTORY */}
      <h3>Booking History</h3>
      {bookings.map(b => (
        <div className={`history ${b.status.toLowerCase()}`} key={b._id}>
          <p><b>Slot:</b> {b.slotId?.slotNo || "Removed Slot"}</p>
          <p>
            {new Date(b.startTime).toLocaleString()} →
            {new Date(b.endTime).toLocaleString()}
          </p>
          <p><b>Status:</b> {b.status}</p>
          <p><b>Paid:</b> ₹ {b.amount}</p>

          {b.status === "BOOKED" && (
            <>
              <button onClick={() => extendBooking(b._id)}>
                Extend +1 Hr
              </button>
              <button onClick={() => cancelBooking(b._id)}>
                Cancel
              </button>
            </>
          )}
        </div>
      ))}
    </div>
  );
}
