import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./AdminDashboard.css";

export default function AdminManage() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const auth = { Authorization: `Bearer ${token}` };

  const [cities, setCities] = useState([]);
  const [areas, setAreas] = useState([]);
  const [slots, setSlots] = useState([]);

  const [cityName, setCityName] = useState("");
  const [areaForm, setAreaForm] = useState({ city: "", name: "" });
  const [slotForm, setSlotForm] = useState({
    city: "",
    area: "",
    slotNo: "",
    pricePerHour: ""
  });

  useEffect(() => {
    fetchCities();
    fetchAreas();
    fetchSlots();
  }, []);

  /* ---------------- CITY ---------------- */
  const fetchCities = async () => {
    const res = await fetch("http://localhost:5000/admin/cities", { headers: auth });
    const data = await res.json();
    setCities(data.map(c => c.name));
  };

  const addCity = async () => {
    await fetch("http://localhost:5000/admin/cities", {
      method: "POST",
      headers: { "Content-Type": "application/json", ...auth },
      body: JSON.stringify({ name: cityName })
    });
    setCityName("");
    fetchCities();
  };

  const removeCity = async (city) => {
    await fetch(`http://localhost:5000/admin/cities/${encodeURIComponent(city)}`, {
      method: "DELETE",
      headers: auth
    });
    fetchCities();
    fetchAreas();
    fetchSlots();
  };

  /* ---------------- AREA ---------------- */
  const fetchAreas = async () => {
    const res = await fetch("http://localhost:5000/admin/areas", { headers: auth });
    setAreas(await res.json());
  };

  const addArea = async () => {
    await fetch("http://localhost:5000/admin/areas", {
      method: "POST",
      headers: { "Content-Type": "application/json", ...auth },
      body: JSON.stringify(areaForm)
    });
    setAreaForm({ city: "", name: "" });
    fetchAreas();
  };

  const removeArea = async (city, name) => {
    await fetch(`http://localhost:5000/admin/areas/${encodeURIComponent(city)}/${encodeURIComponent(name)}`, {
      method: "DELETE",
      headers: auth
    });
    fetchAreas();
    fetchSlots();
  };

  /* ---------------- SLOT ---------------- */
  const fetchSlots = async () => {
    const res = await fetch("http://localhost:5000/admin/slots", { headers: auth });
    setSlots(await res.json());
  };

  const addSlot = async () => {
    await fetch("http://localhost:5000/admin/slots", {
      method: "POST",
      headers: { "Content-Type": "application/json", ...auth },
      body: JSON.stringify({
        city: slotForm.city,
        area: slotForm.area,
        slotNo: slotForm.slotNo,
        pricePerHour: Number(slotForm.pricePerHour)
      })
    });
    setSlotForm({ city: "", area: "", slotNo: "", pricePerHour: "" });
    fetchSlots();
  };

  const removeSlot = async (id) => {
    await fetch(`http://localhost:5000/admin/slots/${id}`, {
      method: "DELETE",
      headers: auth
    });
    fetchSlots();
  };

  return (
    <div className="admin-page">
      <div className="admin-header">
        <h2>Management</h2>
        <button onClick={() => navigate("/admin")}>← Back</button>
      </div>

      <div className="grid-2">
        <div className="card">
          <h3>Add City</h3>
          <input value={cityName} onChange={e => setCityName(e.target.value)} />
          <button onClick={addCity}>Add</button>

          <select id="cityDel">
            <option value="">Delete City</option>
            {cities.map(c => <option key={c}>{c}</option>)}
          </select>
          <button onClick={() => removeCity(document.getElementById("cityDel").value)}>Delete</button>
        </div>

        <div className="card">
          <h3>Add Area</h3>
          <select onChange={e => setAreaForm({ ...areaForm, city: e.target.value })}>
            <option value="">City</option>
            {cities.map(c => <option key={c}>{c}</option>)}
          </select>
          <input onChange={e => setAreaForm({ ...areaForm, name: e.target.value })} />
          <button onClick={addArea}>Add</button>

          <select id="areaDel">
            {areas.map(a => (
              <option key={a._id} value={`${a.city}|${a.name}`}>
                {a.city} → {a.name}
              </option>
            ))}
          </select>
          <button onClick={() => {
            const v = document.getElementById("areaDel").value.split("|");
            removeArea(v[0], v[1]);
          }}>Delete</button>
        </div>
      </div>

      <div className="card">
        <h3>Add Slot</h3>
        <input placeholder="Slot No" onChange={e => setSlotForm({ ...slotForm, slotNo: e.target.value })} />
        <input placeholder="Price" onChange={e => setSlotForm({ ...slotForm, pricePerHour: e.target.value })} />
        <button onClick={addSlot}>Add Slot</button>

        <h3>Delete Slot</h3>
        {slots.map(s => (
          <div key={s._id} className="row">
            {s.slotNo} - {s.city}
            <button onClick={() => removeSlot(s._id)}>✖</button>
          </div>
        ))}
      </div>
    </div>
  );
}
