import { useEffect, useState } from "react";
import "./UserDashboard.css";

export default function UserDashboard() {
  const token = localStorage.getItem("token");
  const auth = { Authorization: `Bearer ${token}` };

  const [slots, setSlots] = useState([]);
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    fetchSlots();
    fetchBookings();
  }, []);

  const fetchSlots = async () => {
    const res = await fetch("http://localhost:5000/slots", { headers: auth });
    setSlots(await res.json());
  };

  const fetchBookings = async () => {
    const res = await fetch("http://localhost:5000/bookings/my", {
      headers: auth
    });
    const data = await res.json();
    setBookings(data.filter(b => b.slotId)); // ðŸ”‘ FIX
  };

  return (
    <div className="user-page">
      <h2>User Dashboard</h2>

      {/* BOOKINGS HISTORY */}
      <h3>Your Bookings</h3>
      <div className="booking-list">
        {bookings.map(b => (
          <div
            key={b._id}
            className={`history ${b.status.toLowerCase()}`}
          >
            <p><strong>Slot:</strong> {b.slotId?.slotNo || "Removed Slot"}</p>
            <p><strong>City:</strong> {b.slotId?.city || "-"}</p>
            <p><strong>Area:</strong> {b.slotId?.area || "-"}</p>
            <p><strong>Status:</strong> {b.status}</p>
            {b.cancelReason && (
              <p><strong>Reason:</strong> {b.cancelReason}</p>
            )}
          </div>
        ))}
      </div>

      {/* AVAILABLE SLOTS */}
      <h3>Available Slots</h3>
      <div className="slot-grid">
        {slots.map(s => (
          <div
            key={s._id}
            className={`slot-card ${s.isBooked ? "booked" : ""}`}
          >
            <h4>{s.slotNo}</h4>
            <p>{s.city} - {s.area}</p>
            <p>â‚¹ {s.pricePerHour}/hr</p>
            <p className="status">
              {s.isBooked ? "BOOKED" : "AVAILABLE"}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}
