import { useEffect, useState } from "react";
import "./AdminDashboard.css";

export default function AdminDashboard() {
  const token = localStorage.getItem("token");
  const auth = { Authorization: `Bearer ${token}` };

  const [tab, setTab] = useState("CITIES"); // CITIES | SLOTS | REPORTS

  const [cities, setCities] = useState([]);
  const [areas, setAreas] = useState([]);
  const [slots, setSlots] = useState([]);
  const [bookings, setBookings] = useState([]);

  const [cityName, setCityName] = useState("");
  const [areaForm, setAreaForm] = useState({ city: "", name: "" });
  const [slotForm, setSlotForm] = useState({
    city: "",
    area: "",
    slotNo: "",
    pricePerHour: ""
  });

  const [cancelId, setCancelId] = useState(null);
  const [reason, setReason] = useState("");

  /* ---------------- LOAD DATA ---------------- */
  useEffect(() => {
    fetchAll();
  }, []);

  const fetchAll = () => {
    fetchCities();
    fetchAreas();
    fetchSlots();
    fetchBookings();
  };

  const fetchCities = async () => {
    const res = await fetch("http://localhost:5000/admin/cities", {
      headers: auth
    });
    setCities(await res.json());
  };

  const fetchAreas = async () => {
    const res = await fetch("http://localhost:5000/admin/areas", {
      headers: auth
    });
    setAreas(await res.json());
  };

  const fetchSlots = async () => {
    const res = await fetch("http://localhost:5000/admin/slots", {
      headers: auth
    });
    setSlots(await res.json());
  };

  const fetchBookings = async () => {
    const res = await fetch("http://localhost:5000/admin/bookings", {
      headers: auth
    });
    setBookings(await res.json());
  };

  /* ---------------- CITY ---------------- */
  const addCity = async () => {
    if (!cityName) return;
    await fetch("http://localhost:5000/admin/cities", {
      method: "POST",
      headers: { "Content-Type": "application/json", ...auth },
      body: JSON.stringify({ name: cityName })
    });
    setCityName("");
    fetchAll();
  };

  const deleteCity = async (name) => {
    if (!window.confirm("Delete city and all related data?")) return;
    await fetch(
      `http://localhost:5000/admin/cities/${encodeURIComponent(name)}`,
      { method: "DELETE", headers: auth }
    );
    fetchAll();
  };

  /* ---------------- AREA ---------------- */
  const addArea = async () => {
    if (!areaForm.city || !areaForm.name) return;
    await fetch("http://localhost:5000/admin/areas", {
      method: "POST",
      headers: { "Content-Type": "application/json", ...auth },
      body: JSON.stringify(areaForm)
    });
    setAreaForm({ city: "", name: "" });
    fetchAll();
  };

  const deleteArea = async (city, name) => {
    if (!window.confirm("Delete area and related slots?")) return;
    await fetch(
      `http://localhost:5000/admin/areas/${city}/${name}`,
      { method: "DELETE", headers: auth }
    );
    fetchAll();
  };

  /* ---------------- SLOT ---------------- */
  const addSlot = async () => {
    await fetch("http://localhost:5000/admin/slots", {
      method: "POST",
      headers: { "Content-Type": "application/json", ...auth },
      body: JSON.stringify(slotForm)
    });
    setSlotForm({ city: "", area: "", slotNo: "", pricePerHour: "" });
    fetchAll();
  };

  const toggleSlot = async (id) => {
    await fetch(`http://localhost:5000/admin/slots/${id}/toggle`, {
      method: "PUT",
      headers: auth
    });
    fetchSlots();
  };

  const deleteSlot = async (id) => {
    if (!window.confirm("Delete slot?")) return;
    await fetch(`http://localhost:5000/admin/slots/${id}`, {
      method: "DELETE",
      headers: auth
    });
    fetchSlots();
  };

  /* ---------------- BOOKINGS ---------------- */
  const cancelBooking = async () => {
    if (!reason) return;
    await fetch(
      `http://localhost:5000/admin/bookings/${cancelId}/cancel`,
      {
        method: "PUT",
        headers: { "Content-Type": "application/json", ...auth },
        body: JSON.stringify({ reason })
      }
    );
    setCancelId(null);
    setReason("");
    fetchBookings();
  };

  /* ---------------- UI ---------------- */
  return (
    <div className="admin-page">

      {/* HEADER */}
      <div className="admin-header">
        <h2>Admin Panel</h2>
        <button
          className="logout-btn"
          onClick={() => {
            localStorage.clear();
            window.location.href = "/";
          }}
        >
          Logout
        </button>
      </div>

      {/* NAVBAR */}
      <div className="admin-nav">
        <button onClick={() => setTab("CITIES")}>Cities & Areas</button>
        <button onClick={() => setTab("SLOTS")}>Parking Slots</button>
        <button onClick={() => setTab("REPORTS")}>Reports</button>
      </div>

      {/* ---------------- CITIES & AREAS ---------------- */}
      {tab === "CITIES" && (
        <div className="card">
          <h3>Add City</h3>
          <input value={cityName} onChange={e => setCityName(e.target.value)} />
          <button onClick={addCity}>Add City</button>

          {cities.map(c => (
            <div key={c.name} className="row">
              {c.name}
              <button onClick={() => deleteCity(c.name)}>✖</button>
            </div>
          ))}

          <h3>Add Area</h3>
          <select
            value={areaForm.city}
            onChange={e => setAreaForm({ ...areaForm, city: e.target.value })}
          >
            <option value="">City</option>
            {cities.map(c => <option key={c.name}>{c.name}</option>)}
          </select>

          <input
            placeholder="Area name"
            value={areaForm.name}
            onChange={e => setAreaForm({ ...areaForm, name: e.target.value })}
          />
          <button onClick={addArea}>Add Area</button>

          {areas.map(a => (
            <div key={a._id} className="row">
              {a.city} → {a.name}
              <button onClick={() => deleteArea(a.city, a.name)}>✖</button>
            </div>
          ))}
        </div>
      )}

      {/* ---------------- SLOTS ---------------- */}
      {tab === "SLOTS" && (
        <div className="card">
          <h3>Add Slot</h3>

          <select
            value={slotForm.city}
            onChange={e => setSlotForm({ ...slotForm, city: e.target.value })}
          >
            <option value="">City</option>
            {cities.map(c => <option key={c.name}>{c.name}</option>)}
          </select>

          <select
            value={slotForm.area}
            onChange={e => setSlotForm({ ...slotForm, area: e.target.value })}
          >
            <option value="">Area</option>
            {areas.filter(a => a.city === slotForm.city)
              .map(a => <option key={a._id}>{a.name}</option>)}
          </select>

          <input
            placeholder="Slot No"
            value={slotForm.slotNo}
            onChange={e => setSlotForm({ ...slotForm, slotNo: e.target.value })}
          />

          <input
            placeholder="Price / Hr"
            value={slotForm.pricePerHour}
            onChange={e =>
              setSlotForm({ ...slotForm, pricePerHour: e.target.value })
            }
          />

          <button onClick={addSlot}>Add Slot</button>

          <div className="slot-grid">
            {slots.map(s => (
              <div key={s._id} className={`slot-card ${s.isBooked ? "booked" : ""}`}>
                <h4>{s.slotNo}</h4>
                <p>{s.city} • {s.area}</p>
                <button onClick={() => toggleSlot(s._id)}>
                  {s.isEnabled ? "Disable" : "Enable"}
                </button>
                <button onClick={() => deleteSlot(s._id)}>Remove</button>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* ---------------- REPORTS ---------------- */}
      {tab === "REPORTS" && (
        <div className="card">
          <h3>Bookings</h3>

          <table>
            <thead>
              <tr>
                <th>User</th>
                <th>Slot</th>
                <th>Status</th>
                <th>Reason</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {bookings.map(b => (
                <tr key={b._id}>
                  <td>{b.userId?.email}</td>
                  <td>{b.slotId?.slotNo || "Removed"}</td>
                  <td>{b.status}</td>
                  <td>{b.cancelReason || "-"}</td>
                  <td>
                    {b.status === "BOOKED" && (
                      <button onClick={() => setCancelId(b._id)}>
                        Cancel
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* CANCEL MODAL */}
      {cancelId && (
        <div className="modal">
          <div className="modal-card">
            <h3>Cancel Booking</h3>
            <textarea
              placeholder="Reason"
              value={reason}
              onChange={e => setReason(e.target.value)}
            />
            <button onClick={cancelBooking}>Confirm</button>
            <button onClick={() => setCancelId(null)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}
