import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./AdminReports.css";

export default function AdminReports() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const auth = { Authorization: `Bearer ${token}` };

  const [bookings, setBookings] = useState([]);
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [city, setCity] = useState("");
  const [area, setArea] = useState("");
  const [slot, setSlot] = useState("");

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = async () => {
    const res = await fetch("http://localhost:5000/admin/bookings", {
      headers: auth
    });
    setBookings(await res.json());
  };

  /* ================= FILTER ================= */
  const filtered = bookings.filter(b => {
    if (!b.slotId) return false;

    const date = new Date(b.startTime);
    if (from && date < new Date(from)) return false;
    if (to && date > new Date(to)) return false;
    if (city && b.slotId.city !== city) return false;
    if (area && b.slotId.area !== area) return false;
    if (slot && b.slotId.slotNo !== slot) return false;

    return true;
  });

  /* ================= ANALYTICS ================= */
  const countBy = (keyFn) =>
    filtered.reduce((acc, x) => {
      const k = keyFn(x);
      if (!k) return acc;
      acc[k] = (acc[k] || 0) + 1;
      return acc;
    }, {});

  const top = (obj) =>
    Object.entries(obj).sort((a, b) => b[1] - a[1])[0]?.[0] || "-";

  const peakHour = top(
    countBy(b => new Date(b.startTime).getHours() + ":00")
  );

  const mostCity = top(countBy(b => b.slotId.city));
  const mostArea = top(countBy(b => b.slotId.area));
  const mostUser = top(countBy(b => b.userId?.email));

  const totalRevenue = filtered.reduce((s, b) => s + (b.amount || 0), 0);

  /* ================= CSV EXPORT ================= */
  const exportCSV = () => {
    const header = [
      "User",
      "City",
      "Area",
      "Slot",
      "Start",
      "End",
      "Hours",
      "Amount",
      "Status"
    ];

    const rows = filtered.map(b => [
      b.userId?.email || "-",
      b.slotId.city,
      b.slotId.area,
      b.slotId.slotNo,
      new Date(b.startTime).toLocaleString(),
      new Date(b.endTime).toLocaleString(),
      b.hours,
      b.amount,
      b.status
    ]);

    const csv =
      [header, ...rows]
        .map(r => r.map(x => `"${x}"`).join(","))
        .join("\n");

    const blob = new Blob([csv], { type: "text/csv" });
    const url = URL.createObjectURL(blob);

    const a = document.createElement("a");
    a.href = url;
    a.download = "parking-report.csv";
    a.click();
  };

  return (
    <div className="admin-report-page">

      {/* HEADER */}
      <div className="admin-header">
        <div>
          <h2>Monthly Usage Report</h2>
          <span>Analytics & Exports</span>
        </div>

        <div style={{ display: "flex", gap: "8px" }}>
          <button className="icon-btn" onClick={exportCSV}>⬇</button>
          <button className="icon-btn" onClick={() => navigate("/admin")}>←</button>
        </div>
      </div>

      {/* FILTERS */}
      <div className="filter-bar">
        <input type="date" onChange={e => setFrom(e.target.value)} />
        <input type="date" onChange={e => setTo(e.target.value)} />
        <input placeholder="City" onChange={e => setCity(e.target.value)} />
        <input placeholder="Area" onChange={e => setArea(e.target.value)} />
        <input placeholder="Slot No" onChange={e => setSlot(e.target.value)} />
      </div>

      {/* ANALYTICS */}
      <div className="stats-grid">
        <Stat title="Total Bookings">{filtered.length}</Stat>
        <Stat title="Revenue">₹ {totalRevenue}</Stat>
        <Stat title="Peak Hour">{peakHour}</Stat>
        <Stat title="Top City">{mostCity}</Stat>
        <Stat title="Top Area">{mostArea}</Stat>
        <Stat title="Top User">{mostUser}</Stat>
      </div>

      {/* TABLE */}
      <div className="table-card">
        <table>
          <thead>
            <tr>
              <th>User</th>
              <th>Slot</th>
              <th>City</th>
              <th>Area</th>
              <th>Status</th>
              <th>Amount</th>
            </tr>
          </thead>
          <tbody>
            {filtered.map(b => (
              <tr key={b._id}>
                <td>{b.userId?.email}</td>
                <td>{b.slotId.slotNo}</td>
                <td>{b.slotId.city}</td>
                <td>{b.slotId.area}</td>
                <td>{b.status}</td>
                <td>₹ {b.amount}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

    </div>
  );
}

/* SMALL STAT */
const Stat = ({ title, children }) => (
  <div className="stat-card">
    <h4>{title}</h4>
    <p>{children}</p>
  </div>
);
